async def main():
    async with aiohttp.ClientSession(
        connector=aiohttp.TCPConnector(ssl=False, limit=0),
        skip_auto_headers={"Accept"}
    ) as session:
        await get_proxy(session)
        await asyncio.gather(*[create_account(session) for _ in range(1)])

try:
    import uvloop
    uvloop.run(main())
except ModuleNotFoundError:
    asyncio.run(main())
